var title_f17_16_17675="Screening SUDs";
var content_f17_16_17675=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F51180&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F51180&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Screening for alcohol and drug use: The CAGE-AID questionnaire",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Item",
"      </td>",
"      <td class=\"subtitle1\">",
"       Text",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       1.",
"      </td>",
"      <td>",
"       Have you ever felt you ought to cut down on your drinking or drug use?",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       2.",
"      </td>",
"      <td>",
"       Have people annoyed you by criticizing your drinking or drug use?",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       3.",
"      </td>",
"      <td>",
"       Have you ever felt bad or guilty about your drinking or drug use?",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       4.",
"      </td>",
"      <td>",
"       Have your ever had a drink or used drugs first thing in the morning to steady your nerves or get rid of a hangover?",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    NOTE: The CAGE-AID is a modification of the CAGE questionnaire, adapted to include drugs in addition to alcohol use. Use of the CAGE-AID should be preceded by the following instruction: \"When thinking about drug use, include illegal drug use and the use of prescription drugs other than as prescribed.\"",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: Brown, RL, Leonard, T, Saunders, LA, Papasouliotis, O. The prevalence and detection of substance use disorder among inpatients ages 18 to 49: An opportunity for prevention. Preventive Medicine 1998; 27:101. Illustration used with permission of Elsevier Inc. All rights reserved.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
