var title_f17_18_17707="ADA opthalmologic exam schedule";
var content_f17_18_17707=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ENDO%2F61833&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=0\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ENDO%2F61833&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=0\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Ophthalmologic examination schedule",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Patient group",
"      </td>",
"      <td class=\"subtitle1\">",
"       Recommended first examination",
"      </td>",
"      <td class=\"subtitle1\">",
"       Minimum routine follow-up*",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Type 1 diabetes",
"      </td>",
"      <td>",
"       Within 5 years after diagnosis of diabetes once patient is age 10 years or older&bull;",
"      </td>",
"      <td>",
"       Yearly",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Type 2 diabetes",
"      </td>",
"      <td>",
"       At time of diagnosis of diabetes",
"      </td>",
"      <td>",
"       Yearly",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Pregnancy in preexisting diabetes",
"      </td>",
"      <td>",
"       Prior to conception and during first trimester. Counsel on the risk of development and/or progression of retinopathy.",
"      </td>",
"      <td>",
"       Close follow-up throughout pregnancy and for one year postpartum.",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Abnormal findings necessitate more frequent follow-up.",
"     <br>",
"      &bull; Some evidence suggests that the prepubertal duration of diabetes may be important in the development of microvascular complications; therefore, clinical judgment should be used when applying these recommendations to individual patients.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Copyright &copy; 2004 American Diabetes Association From Diabetes Care Vol 27, Supplement 1, 2004. Reprinted with permission from The American Diabetes Association. Modifications from Standards of Medical Care in Diabetes--2009. Diabetes Care Vol 32, Supplement 1, 2009.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
