var title_f17_18_17708="Treatable conditions associated with cardiac arrest";
var content_f17_18_17708=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F52416&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F52416&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Treatable conditions associated with cardiac arrest",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Condition",
"       </td>",
"       <td class=\"subtitle1\">",
"        Common associated clinical settings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Acidosis",
"       </td>",
"       <td>",
"        Diabetes, diarrhea, drug overdose, renal dysfunction, sepsis, shock",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Anemia",
"       </td>",
"       <td>",
"        Gastrointestinal bleeding, nutritional deficiencies, recent trauma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cardiac tamponade",
"       </td>",
"       <td>",
"        Post-cardiac surgery, malignancy, post-myocardial infarction, pericarditis, trauma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hyperkalemia",
"       </td>",
"       <td>",
"        Drug overdose, renal dysfunction, hemolysis, excessive potassium intake, rhabdomyolysis, major soft tissue injury, tumor lysis syndrome",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hypokalemia*",
"       </td>",
"       <td>",
"        Alcohol abuse, diabetes mellitus, diuretics, drug overdose, profound gastrointestinal losses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hypothermia",
"       </td>",
"       <td>",
"        Alcohol intoxication, significant burns, drowning, drug overdose, elder patient, endocrine disease, environmental exposure, spinal cord disease, trauma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hypovolemia",
"       </td>",
"       <td>",
"        Significant burns, diabetes, gastrointestinal losses, hemorrhage, malignancy, sepsis, trauma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hypoxia",
"       </td>",
"       <td>",
"        Upper airway obstruction, hypoventilation (CNS dysfunction, neuromuscular disease), pulmonary disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Myocardial infarction",
"       </td>",
"       <td>",
"        Cardiac arrest",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Poisoning",
"       </td>",
"       <td>",
"        History of alcohol or drug abuse, altered mental status, classic toxidrome (eg, sympathomimetic), occupational exposure, psychiatric disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pulmonary embolism",
"       </td>",
"       <td>",
"        Immobilized patient, recent surgical procedure (eg, orthopedic), peripartum, risk factors for thromboembolic disease, recent trauma, presentation consistent with acute pulmonary embolism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tension pneumothorax",
"       </td>",
"       <td>",
"        Central venous catheter, mechanical ventilation, pulmonary disease (eg, asthma, chronic obstructive pulmonary disease), thoracentesis, thoracic trauma",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Hypomagnesemia should be assumed in the setting of hypokalemia, and both should be treated.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Eisenberg MS, Mengert TJ. Cardiac resuscitation. N Engl J Med 2001; 344:1304.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
