var title_f17_19_17723="Antitrypanosomal drugs dosing";
var content_f17_19_17723=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F54443&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F54443&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Antitrypanosomal drugs for treatment of Chagas disease",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Drug",
"       </td>",
"       <td class=\"subtitle1\">",
"        Age group",
"       </td>",
"       <td class=\"subtitle1\">",
"        Dosage",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Benznidazole",
"       </td>",
"       <td>",
"        &le;12 years",
"       </td>",
"       <td>",
"        10 mg/kg per day orally in two divided doses for 60 days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        12 years or older",
"       </td>",
"       <td>",
"        5 to 7 mg/kg per day orally in two divided doses for 60 days",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"3\">",
"        Nifurtimox",
"       </td>",
"       <td>",
"        &le;10 years",
"       </td>",
"       <td>",
"        15 to 20 mg/kg per day orally in three or four divided doses for 90 days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        11 to 16 years",
"       </td>",
"       <td>",
"        12.5 to 15 mg/kg per day orally in three or four divided doses for 90 days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        17 years or older",
"       </td>",
"       <td>",
"        8 to 10 mg/kg per day orally in three or four divided doses for 90 to 120 days",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Please see text for additional notes regarding use, adverse effects, and availability.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
