" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Exocrine pancreatic cancer: Palliation of symptoms",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?17/20/17735/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
