var title_f17_25_17819="ASGE guideline droperidol";
var content_f17_25_17819=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F54264&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F54264&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Guidelines for the use of droperidol for endoscopic procedures",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Use only in select patients with:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Anticipated intolerance of standard sedatives",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Anticipated long procedure time",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Obtain 12-lead ECG. Droperidol is contraindicated if the QTc is prolonged (&gt;440 msec males, &gt;450 msec females).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Patients should remain on a cardiac monitor during the procedure and for 2 to 3 hours afterward",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Use with caution in patients at risk for development of prolonged QT syndrome: HF, bradycardia, cardiac hypertrophy, hypokalemia/magnesemia, on other drugs known to prolong the QT interval",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dosage: in adults the initial dosage should not exceed 2.5 mg. Additional doses should be in 1.25 mg aliquots to achieve the desired effect. Maximum dose: 5 mg.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Faigel DO, Baron TH, Goldstein JL, et al. Guidelines for the use of deep sedation and anesthesia for GI endoscopy. Gastrointest Endosc 2002; 56:613. Copyright &copy; 2002 Elsevier Science.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
