var title_f17_26_17835="Criteria for outpatient liver biopsy";
var content_f17_26_17835=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F57997&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F57997&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Criteria for outpatient liver biopsy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        The patient must be able to return to the hospital in which",
"the procedure was performed within 30 minutes of any adverse symptoms.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        The patient must have reliable persons stay with him or her during the first night after the biopsy to provide care and transportation, if necessary.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        The patient",
"should have no complications or associated serious medical problems",
"that increase the risk of the biopsy.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        The facility",
"in which the biopsy is performed should have approved laboratory, blood",
"banking unit, easy access to an inpatient bed, and personnel to monitor",
"the patient for at least six hours* after the biopsy.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        The patient",
"should be hospitalized after the biopsy if there is evidence of",
"bleeding, bile leak, pneumothorax, or other organ puncture, or if",
"post-biopsy pain requires more than one dose of analgesics in the first",
"four hours after biopsy.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Many centers currently monitor patients for only&nbsp;three to&nbsp;four hours.",
"    </div>",
"    <div class=\"reference\">",
"     From: Jacobs WH, Goldberg SB. Dig Dis Sci 1989; 34:322.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
