" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Examples of prehospital triage scoring systems",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellpadding=\"0\" cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Name",
"      </td>",
"      <td class=\"subtitle1_single\">",
"       Parameters",
"      </td>",
"      <td class=\"subtitle1_single\">",
"       Notes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Prehospital Index",
"      </td>",
"      <td>",
"       <p>",
"        Systolic BP",
"       </p>",
"       <p>",
"        Pulse rate",
"       </p>",
"       <p>",
"        Respiratory rate",
"       </p>",
"       <p>",
"        Consciousness",
"       </p>",
"       <p>",
"        Penetrating wounds (chest or abdomen)",
"       </p>",
"      </td>",
"      <td>",
"       Scale is 0 to 24 (&gt;3: major trauma)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       CRAMS (Circulation, Respiration, Abdomen/Thorax, Motor, Speech)",
"      </td>",
"      <td>",
"       <p>",
"        Systolic BP or capillary refill",
"       </p>",
"       <p>",
"        Respirations",
"       </p>",
"       <p>",
"        Examination of trunk",
"       </p>",
"       <p>",
"        Motor function",
"       </p>",
"       <p>",
"        Speech pattern",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        Scale is 0 to 10 (&lt;8: major trauma)",
"       </p>",
"       <p>",
"        Each parameter rated as normal, mildly abnormal, or highly abnormal",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Revised Trauma Score",
"      </td>",
"      <td>",
"       <p>",
"        Systolic BP",
"       </p>",
"       <p>",
"        Respiratory rate",
"       </p>",
"       <p>",
"        GCS",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        0 to 7.8408 (&lt;4: major trauma)",
"       </p>",
"       <p>",
"        GCS more heavily weighted",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       START (Simple triage and rapid treatment)",
"      </td>",
"      <td>",
"       <p>",
"        Ambulation",
"       </p>",
"       <p>",
"        Respiratory rate",
"       </p>",
"       <p>",
