" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Histopathology report of melanoma",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Essential data",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Diagnosis: Malignant melanoma, in situ or invasive",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Breslow thickness: mm",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Ulceration: present or absent",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Mitoses: number/mm",
"       <sup>",
"        2",
"       </sup>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Microscopic satellites: Present or absent",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Vascular invasion: Present or absent",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Surgical margins: Adequacy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Other prognostic data",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Clark Level: I, II, III, IV, V",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Growth phase: Radial or vertical",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Regression: Present or absent",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       TIL's: Present (brisk or non-brisk) or absent",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Histologic type: eg, Superficial spreading, desmoplastic",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
