var title_f17_31_17916="Best pract infection prevention";
var content_f17_31_17916=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F58579&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F58579&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Best practices for preventing surgical site infections",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Evidence category IA - Well designed studies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cancel elective surgery if the patient has an infection at or remote from the surgical site",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Achieve maximal subcutaneous concentration of perioperative antibiotics",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Maintain prophylactic antibiotics for only a few hours after closing incisions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        For high-risk cesarean, administer the prophylactic antimicrobial immediately after the umbilical cord is clamped",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        If it is necessary to remove hair, use clippers, not shaving, immediately before operation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Evidence category IB - Good evidence and expert consensus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Control glucose levels in diabetic patients and avoid perioperative hyperglycemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Encourage patients who use tobacco products to quit using or to abstain for 30 days prior to surgery",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Have the patient shower or bathe with an antiseptic agent on at least the night before surgery",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Follow strict standards for sterilizing instruments, disinfecting operating room, and air circulation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Do not routinely use vancomycin for prophylaxis if other agents are appropriate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Do not use UV radiation in the operating room for infection prophylaxis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Surgical staff who have draining skin lesions are excluded from duty",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Surgical staff should wear sterile clothing and gloves",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Surgical team hand hygiene to include keeping fingernails short, scrubing with antiseptic to elbows for&nbsp;two to five&nbsp;minutes, using sterile towels",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Use appropriate topical microbicides during surgery",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Use proper surgical technique",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Apply sterile dressing to incision for 24 to 48 hours postoperatively and wash hands before contact with surgical site",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Perform hospital surveillance for surgical site infection",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from the Centers for Disease Control Guidelines for Prevention of Surgical Site Infection (www.cdc.gov/ncidod/dhqp/gl_surgicalsite.html).",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
