var title_f17_32_17931="Infection rates of open fractures";
var content_f17_32_17931=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F72480&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F72480&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Classification system for open fracture and related infection rates",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Fracture type",
"       </td>",
"       <td class=\"subtitle1\">",
"        Definition",
"       </td>",
"       <td class=\"subtitle1\">",
"        Historical infection rates (percentage)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        I",
"       </td>",
"       <td>",
"        Wound &lt;1 cm; minimal contamination, comminution and soft-tissue damage",
"       </td>",
"       <td>",
"        0-2",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        II",
"       </td>",
"       <td>",
"        Wound &gt;1 cm; moderate soft-tissue damage, minimal periosteal stripping",
"       </td>",
"       <td>",
"        2-5",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        III",
"       </td>",
"       <td>",
"        Severe soft-tissue damage and substantial contamination; coverage adequate",
"       </td>",
"       <td>",
"        5-10",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        IV",
"       </td>",
"       <td>",
"        Sever soft-tissue damage and substantial contamination; coverage inadequate",
"       </td>",
"       <td>",
"        10-50",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        V",
"       </td>",
"       <td>",
"        Arterial injury requiring repair",
"       </td>",
"       <td>",
"        25-50",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Okike K, Bhattacharyya T. Trends in the management of open fractures. A critical analysis. J Bone Joint Surg Am 2006; 88:2739. Copyright &copy; 2006 Journal of Bone and Joint Surgery, Inc.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
