var script_f17_35_17969=[" ",
"",
"",
"function log(i){",
"return Math.log(i) * Math.LOG10E;   ",
"}",
"",
"function ln(i){",
"return Math.log(i);",
"}",
"",
"function sq(i){",
"return i * i;",
"}",
"",
"function sqr(i){",
"return Math.sqrt(i);",
"}",
"",
"",
"function power(x,y){",
"return Math.pow(x,y);",
"}",
"",
"function eTo(x){",
"return Math.exp(x);",
"}",
"",
"",
"function fixDP(r, dps) {",
"if (isNaN(r)) return \"NaN\";",
"var msign = '';",
"if (r < 0) msign = '-';",
"x = Math.abs(r);",
"if (x > Math.pow(10, 21)) return msign + x.toString();",
"var m = Math.round(x * Math.pow(10, dps)).toString();",
"if (dps == 0) return msign + m;",
"while (m.length <= dps) m = \"0\" + m;",
"return msign + m.substring(0, m.length - dps) + \".\" + m.substring(m.length - dps);",
"}",
"",
"var currenttimeout;",
"",
"",
"var calctxt = ''; ",
"var xmltxt = ''; ",
"var htmtxt = ''; ",
"",
"",
"",
"function CalciumAlbumin_fx() {",
"",
"currenttimeout = self.setTimeout('minMaxCheck();', 3000);",
"",
"with(document.CalciumAlbumin_form){",
"",
"",
"doCalc = true;",
"if (Serum_Ca_param.value.indexOf(',') >= 0){ Serum_Ca_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Serum_Ca_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Serum_Ca_unit.options[Serum_Ca_unit.selectedIndex].value.split('|');",
"Serum_Ca = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (Normal_Albumin_radio[0].checked){ Normal_Albumin = 4; }",
"if (Normal_Albumin_radio[1].checked){ Normal_Albumin = 4.4; }",
"if (Patient_Albumin_param.value.indexOf(',') >= 0){ Patient_Albumin_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Patient_Albumin_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Patient_Albumin_unit.options[Patient_Albumin_unit.selectedIndex].value.split('|');",
"Patient_Albumin = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"dp = decpts.options[decpts.selectedIndex].text;",
"Ca =  Serum_Ca + 0.8 * (Normal_Albumin - Patient_Albumin);",
"",
"unit_parts = Ca_unit.options[Ca_unit.selectedIndex].value.split('|');",
"if (doCalc) Ca_param.value = fixDP((Ca - parseFloat(unit_parts[1])) / parseFloat(unit_parts[0]), dp);",
"",
"",
"",
"",
"",
"}",
"",
"}",
"",
"function minMaxCheck(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.CalciumAlbumin_form){",
"",
"",
"",
"}",
"",
"}",
"",
"",
"function clrResults(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.CalciumAlbumin_form){",
"",
"Ca_param.value = '';",
"",
"",
"}",
"",
"}",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
""].join("\n");
