var title_f17_35_17981="Oral agents for mild to moderate diabetic foot infections";
var content_f17_35_17981=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F69671&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F69671&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Oral agents for empiric treatment of mild to moderate diabetic foot infections",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"2\" width=\"50%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        SINGLE-drug regimens with activity against streptococci and staphylococci (MSSA)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"2\">",
"        Cephalexin",
"        <strong>",
"         or",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"2\">",
"        Dicloxacillin",
"        <strong>",
"         or",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"2\">",
"        <p>",
"         Amoxicillin-clavulanate",
"         <strong>",
"          or",
"         </strong>",
"         clindamycin",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        TWO-drug regimens with activity against streptococci and MRSA",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"2\">",
"        Clindamycin*",
"        <strong>",
"         or",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"2\">",
"        Linezolid",
"        <strong>",
"         or",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"2\">",
"        <p>",
"         Penicillin",
"         <strong>",
"          or",
"         </strong>",
"         cefazolin",
"         <strong>",
"          or",
"         </strong>",
"         dicloxacillin",
"        </p>",
"        <p>",
"         <strong>",
"          PLUS",
"         </strong>",
"        </p>",
"        Trimethoprim-sulfamethoxazole",
"        <strong>",
"         or",
"        </strong>",
"        doxycycline",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        TWO-drug regimens with activity against streptococci, MRSA, aerobic gram-negative bacilli and anaerobes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"2\">",
"        <p>",
"         Trimethoprim-sulfamethoxazole",
"        </p>",
"        <p>",
"         <strong>",
"          PLUS",
"         </strong>",
"        </p>",
"        <p>",
"         Amoxicillin-clavulanate",
"        </p>",
"        <p>",
"         <strong>",
"          -OR-",
"         </strong>",
"        </p>",
"        <p>",
"         Clindamycin",
"        </p>",
"        <p>",
"         <strong>",
"          PLUS",
"         </strong>",
"        </p>",
"        <p>",
"         Ciprofloxacin",
"         <strong>",
"          or",
"         </strong>",
"         levofloxacin",
"         <strong>",
"          or",
"         </strong>",
"         moxifloxacin",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Antibiotic dosing for adults",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Cephalexin",
"       </td>",
"       <td class=\"sublist_other\">",
"        500 mg every 6 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Dicloxacillin",
"       </td>",
"       <td class=\"sublist_other\">",
"        500 mg every 6 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Clindamycin",
"       </td>",
"       <td class=\"sublist_other\">",
"        300 to 450 mg every 6 to 8 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Linezolid",
"       </td>",
"       <td class=\"sublist_other\">",
"        600 mg every 12 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Penicillin V potassium",
"       </td>",
"       <td class=\"sublist_other\">",
"        500 mg every 6 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Trimethoprim-sulfamethoxazole (co-trimoxazole)",
"       </td>",
"       <td class=\"sublist_other\">",
"        2 double-strength tablets (trimethoprim 160 mg and sulfamethoxazole 800 mg per tablet)&nbsp;every 12 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Doxycycline",
"       </td>",
"       <td class=\"sublist_other\">",
"        100 mg orally every 12 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Amoxicillin-clavulanate",
"       </td>",
"       <td class=\"sublist_other\">",
"        875/125 mg every 12 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Ciprofloxacin",
"       </td>",
"       <td class=\"sublist_other\">",
"        750 mg every 12 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Levofloxacin",
"       </td>",
"       <td class=\"sublist_other\">",
"        750 mg every 24 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Moxifloxacin",
"       </td>",
"       <td class=\"sublist_other\">",
"        400 mg every 24 hours",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     MSSA: methicillin-susecptible staphylococcus aureus; MRSA: methicillin-resistant staphylococcus aureus.",
"     <br>",
"      * Check susceptibility testing.",
"      <br>",
"       <font class=\"bullet\">",
"        &bull;",
"       </font>",
"       Many of these agents require adjustment of the dose in the setting of renal dysfunction.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Data courtesy of authors with additional data from: Lipsky BA, et al. 2012 Infectious Diseases Society of America clinical practice guideline for the diagnosis and treatment of diabetic foot infections. Clin Infect Dis 2012;&nbsp;54:e132.&nbsp;&nbsp;",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
