" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Prevention and treatment of glucocorticoid-induced osteoporosis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?17/36/17994/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?17/36/17994/contributors\">",
"     Harold N Rosen, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?17/36/17994/contributors\">",
"     Kenneth G Saag, MD, MSc",
