var title_f17_36_17995="Emergency pericardiocentesis equipment";
var content_f17_36_17995=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F72290&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F72290&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Equipment for emergency pericardiocentesis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Sterile gown, gloves, and mask",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sterile barrier drapes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chlorhexidine-based skin preparation solution",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        20 to 25-gauge needle for local anesthetic infiltration",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Syringes (10 and 60 mL)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Three-way stopcock",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Plastic drainage tubing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        18-gauge 7 cm large bore introducer needle",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Flexible or curved-tip (J) guidewire",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         6 to 8 French drainage catheter (pigtail, sheath, or central venous catheter)",
"        </p>",
"        <p>",
"         (Supplies in a standard 7 French central venous catheter set are good immediate substitutes)",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Echocardiographic system with 2-5 MHz transducer",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ultrasound needle guide",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    The list above includes all equipment that may be necessary to perform emergency pericardiocentesis. Time to obtain all materials may not be available in life-threatening circumstances.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
