var title_f17_39_18045="Assessment for weight-related comorbidities";
var content_f17_39_18045=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F65547&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F65547&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Additional assessment for weight-related comorbidities to be considered for selected children with obesity",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"4\" width=\"25%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Condition",
"       </td>",
"       <td class=\"subtitle1\">",
"        Tests",
"       </td>",
"       <td class=\"subtitle1\">",
"        Reason",
"       </td>",
"       <td class=\"subtitle1\">",
"        Note",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cardiac disease",
"       </td>",
"       <td>",
"        Lipid profile",
"       </td>",
"       <td>",
"        Hyperlipidemia, hypertriglyceridemia, cardiovascular disease risk",
"       </td>",
"       <td>",
"        Fasting sample preferred",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"3\">",
"        Hypertension",
"       </td>",
"       <td>",
"        Repeat BP measurements on several occasions",
"       </td>",
"       <td>",
"        Required to diagnose or exclude hypertension",
"       </td>",
"       <td>",
"        Use appropriately sized cuffs and age-appropriate norms",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        24-hour ambulatory BP monitoring",
"       </td>",
"       <td>",
"        Evaluate for \"masked\" hypertension; rule out \"white coat\" hypertension",
"       </td>",
"       <td>",
"        Suggested if the diagnosis is unclear from random office measurements",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        CBC, metabolic panel, renin assay, urinalysis, renal ultrasound",
"       </td>",
"       <td>",
"        Exclude other causes of hypertension",
"       </td>",
"       <td>",
"        Suggested if hypertension is confirmed",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        Fatty liver disease",
"       </td>",
"       <td>",
"        Liver ultrasound; &alpha;-1-antitrypsin, ceruloplasmin, ANA, hepatitis antibodies",
"       </td>",
"       <td>",
"        Determine cause of elevated transaminases",
"       </td>",
"       <td>",
"        Persistent elevation of AST, ALT for &gt;6 months warrants further investigation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Liver biopsy",
"       </td>",
"       <td>",
"        Determine cause of elevated transaminases, assess degree of hepatitis",
"       </td>",
"       <td>",
"        Imaging cannot accurately determine inflammation and fibrosis",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Type 2 diabetes mellitus or impaired glucose tolerance",
"       </td>",
"       <td>",
"        Fasting glucose, oral glucose tolerance test, HbA1c, urinary microalbumin",
"       </td>",
"       <td>",
"        Assess for insulin resistance, renal involvement",
"       </td>",
"       <td>",
"        Fasting glucose &ge;126 mg/dL or HbA1c &ge;6.5 percent indicates diabetes. Fasting glucose 100-125 mg/dL or HbA1c 5.7-6.4 percent considered pre-diabetes.",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Sleep apnea",
"       </td>",
"       <td>",
"        Polysomnogram (sleep study)",
"       </td>",
"       <td>",
"        Evaluate sleep related breathing disorders",
"       </td>",
"       <td>",
"        Polysomnogram also may detect disordered sleep pattern",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        Orthopedic disease",
"       </td>",
"       <td>",
"        Hip x-rays",
"       </td>",
"       <td>",
"        Evaluate for SCFE",
"       </td>",
"       <td>",
"        Use frog-leg positioning for radiograph",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Knee x-rays",
"       </td>",
"       <td>",
"        Evaluate for Blount disease",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Polycystic ovary syndrome",
"       </td>",
"       <td>",
"        17-hydroxyprogesterone, DHEAS, androstenedione, free testosterone (or total testosterone and SHBG), LH, FSH, possibly pelvic ultrasound",
"       </td>",
"       <td>",
"        To confirm whether hyperandrogenemia is present, and exclude other causes of hyperandrogenemia",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Precocious puberty",
"       </td>",
"       <td>",
"        LH, FSH, testosterone or estradiol, DHEAS",
"       </td>",
"       <td>",
"        Early onset of obesity",
"       </td>",
"       <td>",
"        Physical exam often is sufficient to evaluate",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Pseudotumor cerebri",
"       </td>",
"       <td>",
"        Fundoscopic exam, lumbar puncture",
"       </td>",
"       <td>",
"        Increased intracranial pressure suggested by papilledema, and confirmed by lumbar puncture",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     BP: blood pressure; CBC: complete blood count; AST: aspartate aminotransferase; ALT: alanine aminotransferase; ANA: antinuclear antibodies; HbA1c: hemoglobin A1c; SCFE: slipped capital femoral epiphysis; DHEAS: dehydroepiandrosterone sulfate; SHBG: sex hormone binding globulin; LH: luteinizing hormone; FSH: follicle-stimulating hormone.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from:",
"     <br/>",
"     <ol>",
"      <li>",
"       Barlow SE. Expert committee recommendations regarding the prevention, assessment, and treatment of child and adolescent overweight and obesity: summary report. Pediatrics. 2007 Dec; 120 Suppl 4:S164-92.",
"      </li>",
"      <li>",
"       Krebs NF, et al. Assessment of child and adolescent overweight and obesity. Pediatrics. 2007 Dec; 120 Suppl 4:S193-228.",
"      </li>",
"     </ol>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
