var title_f17_40_18060="Empiric treatment for pyelonephritis in pregnancy";
var content_f17_40_18060=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F56181&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F56181&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Parenteral regimens for empiric treatment of pyelonephritis in pregnancy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Antibiotic",
"       </td>",
"       <td class=\"subtitle1\">",
"        Dose, interval",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Mild to moderate pyelonephritis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ceftriaxone",
"       </td>",
"       <td>",
"        1 g every 24 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cefepime",
"       </td>",
"       <td>",
"        1 g every 12 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Aztreonam*",
"       </td>",
"       <td>",
"        1 g every 8 to 12 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &nbsp;",
"        <p>",
"         Ampicillin",
"        </p>",
"        <p>",
"         PLUS",
"        </p>",
"        <p>",
"         Gentamicin&bull;",
"        </p>",
"       </td>",
"       <td>",
"        &nbsp;",
"        <p>",
"         1-2 g every 6 hours",
"        </p>",
"        <p>",
"         &nbsp;",
"        </p>",
"        <p>",
"         1.5 mg/kg every 8 hours",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Severe pyelonephritis with immunocompromise and/or incomplete urinary drainage",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ticarcillin-clavulanate",
"       </td>",
"       <td>",
"        3.1 g every 6 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Piperacillin-tazobactam",
"       </td>",
"       <td>",
"        3.375 g every 6 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Meropenem",
"       </td>",
"       <td>",
"        500 mg every 8 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ertapenem&nbsp;",
"       </td>",
"       <td>",
"        1 g every 24 hours&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Doripenem",
"       </td>",
"       <td>",
"        500 mg every&nbsp;8 hours",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Doses are for patients with normal renal function.",
"    <br>",
"     The treatment of urinary tract infection due to enterococcus is discussed separately.",
"     <br>",
"      If methicillin-resistant S. aureus (MRSA) is known or suspected, see treatment regimens outlined separately in topics addressing MRSA management.",
"      <div class=\"footnotes\">",
"       * Alternative in the setting of beta lactam allergy.",
"       <br>",
"        &bull; Aminoglycosides have been associated with fetal ototoxicity (United States Food and Drug Administration&nbsp;category D); &nbsp;this regimen should be used only if intolerance precludes the use of less toxic agents.",
"       </br>",
"      </div>",
"      <div class=\"reference\">",
"      </div>",
"     </br>",
"    </br>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
