var title_f17_41_18075="Identifying asthmatic patients";
var content_f17_41_18075=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F73952&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F73952&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Approaches for identifying persons with asthma",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Method",
"       </td>",
"       <td class=\"subtitle1\">",
"        Problems",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Clinical evaluation",
"       </td>",
"       <td>",
"        Nonstandardization of physician criteria for diagnosis; bias toward more severe cases; bias by access to physician",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Questionnaire history of diagnosis",
"       </td>",
"       <td>",
"        Same as above; plus recall bias in subjects",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Questionnaire history of symptoms",
"       </td>",
"       <td>",
"        Possibly influenced by frequency of other symptoms; less specific than physician's diagnosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Response to bronchodilator",
"       </td>",
"       <td>",
"        May be influenced by level of FEV",
"        <sub>",
"         1",
"        </sub>",
"        correlation with bronchodilator; may lack sensitivity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bronchoconstrictor response",
"       </td>",
"       <td>",
"        Influenced by level of FEV",
"        <sub>",
"         1",
"        </sub>",
"        ; may be nonspecific",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     FEV",
"     <sub>",
"      1",
"     </sub>",
"     : forced expiratory volume in one second.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Weiss ST, Speizer FE. Epidemiology and natural history. In: Bronchial Asthma: Mechanisms and Therapeutics, 3rd ed, Weiss EB, Stein M (Eds), Little, Brown, Boston 1993.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
