"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Ill or medicated individual",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        1. Drugs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Insulin or insulin secretagogue",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Alcohol",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Others (refer to UpToDate table on&nbsp;drugs that cause hypoglycemia)",
"       </td>",
"      </tr>",
"      <tr>",
