var title_f17_43_18108="Liver transplant RAI";
var content_f17_43_18108=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F74243&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F74243&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Histologic rejection activity index for liver transplants",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Category",
"       </td>",
"       <td class=\"subtitle1\">",
"        Criteria",
"       </td>",
"       <td class=\"subtitle1\">",
"        Score",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Portal inflammation",
"       </td>",
"       <td>",
"        Mostly lymphocytic inflammation involving, but not noticeably expanding, a minority of the triads",
"       </td>",
"       <td>",
"        1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Expansion of most of all of the triads, by a",
"mixed infiltrate containing lymphocytes with",
"occasional blasts, neutrophils and eosinophils",
"       </td>",
"       <td style=\"text-align: left;\">",
"        2",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Marked expansion of most or all of the triads by",
"a mixed infiltrate containing numerous blasts and eosinophils",
"with inflammatory spillover into the periportal parenchyma",
"       </td>",
"       <td>",
"        3",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Bile duct inflammation damage",
"       </td>",
"       <td>",
"        A minority of the ducts are cuffed",
"and infiltrated by inflammatory cells and show only mild",
"reactive changes such as increased nuclear:cytoplasmic ratio",
"of the epithelial cells",
"       </td>",
"       <td>",
"        1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Most or all of the ducts infiltrated by",
"inflammatory cells. More than an occasional duct shows",
"degenerative changes such as nuclear pleomorphism,",
"disordered polarity and cytoplasmic vacuolization of",
"the epithelium",
"       </td>",
"       <td>",
"        2",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        As above for 2, with most or all of the ducts showing degenerative changes or focal lumenal disruption",
"       </td>",
"       <td>",
"        3",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Venous endothelial inflammation",
"       </td>",
"       <td>",
"        Subendothelial lymphocytic infiltration involving some, but not a majority of the portal and/or hepatic venules",
"       </td>",
"       <td>",
"        1",
"        <br/>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Subendothelial lymphocytic infiltration involving some, but not a majority of the portal and/or hepatic venules",
"       </td>",
"       <td>",
"        2",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        As above for 2, with moderate or severe",
"perivenular inflammation that extends into the",
"perivenular parenchyma and is associated with",
"perivenular hepatocyte necrosis",
"       </td>",
"       <td>",
"        3",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    The total score is equal to the sum of the scores in each of the three categories.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Demetris AJ, Batta KP, Dhillon AP, et al. Hepatology 1997; 25:658.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
