var title_f17_45_18140="Mgmt antiviral resist HBV";
var content_f17_45_18140=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F68845&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F68845&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Management of antiviral-resistant HBV",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        Prevention",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\">",
"        Avoid unnecessary treatment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\">",
"        Initiate treatment with potent antiviral that has high barrier to drug resistance or with combination therapy if drugs with low barrier to resistance are used",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\">",
"        Switch to alternative therapy in patients with primary non-response",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        Monitoring",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\">",
"        Test for serum HBV DNA (PCR assay) every 3-6 months during treatment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\">",
"        Check for medication compliance in patients with virologic breakthrough",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\">",
"        Confirm antiviral resistance with genotypic testing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        Treatment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Lamivudine or telbivudine-resistance &rarr;",
"       </td>",
"       <td>",
"        Add adefovir*, switch to&nbsp;tenofovir",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stop lamivudine, switch to Truvada&bull;&Delta;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        Adefovir-resistance &rarr;",
"       </td>",
"       <td>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stop adefovir, switch to Truvada&bull;&Delta;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Switch to or add entecavir&Delta;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Entecavir-resistance &rarr;",
"       </td>",
"       <td>",
"        Switch to tenofovir or Truvada&Delta;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Tenofovir-resistance&loz; &rarr;",
"       </td>",
"       <td>",
"        Switch to entecavir",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stop tenofovir, switch to Truvada",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * In countries where tenofovir is not available.",
"     <br>",
"      &bull; Truvada = combination pill with emtricitabine 200 mg and tenofovir 300 mg.",
"      <br>",
"       &Delta; Not approved for non HIV infected persons.",
"       <br>",
"        &loz; Clinical data not available.",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Adapted with permission from: Lok ASF, McMahon BJ. Chronic hepatitis B: Update 2009. Hepatology 2009; 50:661. Available online at file://publish.aasld.org/Pages/Default.aspx. Accessed September 8th 2009. Copyright &copy; 2009 American Association for the Study of Liver Diseases.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
var script_f17_45_18140=[""].join("\n");
