var title_f17_47_18172="Etiology epistaxis children";
var content_f17_47_18172=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F67540&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F67540&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Causes of epistaxis in children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        Local",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Trauma",
"       </td>",
"       <td>",
"        Nose-picking*; foreign body; blunt or penetrating facial trauma; child abuse, postoperative; nasotracheal intubation; nasogastric tube placement",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Mucosal irritation",
"       </td>",
"       <td>",
"        Dry air*; allergic rhinitis; inhaled irritants/drugs (inhaled corticosteroids*, tobacco, cocaine heroin, volatile inhalants of abuse); upper respiratory infection or systemic infection with nasal congestion; localized skin or soft tissue infection",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Anatomic",
"       </td>",
"       <td>",
"        Septal deviation or unilateral choanal atresia causing asymmetric airflow",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Tumors",
"       </td>",
"       <td>",
"        Hemangioma, juvenile nasopharyngeal angiofibroma (particularly in adolescent males), pyogenic granuloma, rhabdomyosarcoma, nasopharyngeal carcinoma, inverting papilloma",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td class=\"indent1\">",
"        Other",
"       </td>",
"       <td>",
"        Increased nasal venous pressure secondary to paroxysmal coughing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\" colspan=\"2\">",
"        Systemic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Bleeding disorders",
"       </td>",
"       <td>",
"        Inherited or acquired coagulation disorders, platelet disorders, blood vessel disorders (eg, hereditary hemorrhagic telangiectasia)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Medications",
"       </td>",
"       <td>",
"        Aspirin, ibuprofen, anticoagulants (including rodenticides), valproic acid",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Granulomatous disorders",
"       </td>",
"       <td>",
"        Granulomatosis with polyangiitis (Wegener's), sarcoidosis, tuberculosis",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td class=\"indent1\">",
"        Hypertension",
"       </td>",
"       <td>",
"        Primary hypertension, secondary hypertension (renal disease, corticosteroids), increased venous pressure (exertion, superior vena cava syndrome)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Recurrent",
"       </td>",
"       <td>",
"        Usually related recurrent/chronic nasal trauma or irritation, but may be the presenting symptom of bleeding disorder, hereditary hemorrhagic telangiectasia, nasopharyngeal carcinoma, posttraumatic pseudoaneurysm of internal carotid artery",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Most common causes.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
