"  <h1>",
"   TOPIC OUTLINE",
"  </h1>",
"  <div id=\"outline\">",
"   <ul>",
"    <li>",
"     <a class=\"sr_button\" href=\"#H27\" id=\"summRecButton\">",
"      <span>",
"       SUMMARY &amp; RECOMMENDATIONS",
"      </span>",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#H1\">",
"      INTRODUCTION",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#H2\">",
"      HYDROPHILIC/SOFT LENSES",
"     </a>",
"    </li>",
"    <li class=\"bulletItem\">",
"     <a class=\"outlineLink\" href=\"#H3\">",
"      Composition",
"     </a>",
"    </li>",
"    <li class=\"bulletItem\">",
"     <a class=\"outlineLink\" href=\"#H4\">",
"      Length of wear",
"     </a>",
"    </li>",
"    <li class=\"dashItem\">",
"     <a class=\"outlineLink\" href=\"#H5\">",
"      - One day lenses",
"     </a>",
"    </li>",
"    <li class=\"dashItem\">",
"     <a class=\"outlineLink\" href=\"#H6\">",
"      - Two week disposable",
"     </a>",
"    </li>",
"    <li class=\"dashItem\">",
"     <a class=\"outlineLink\" href=\"#H7\">",
"      - Monthly disposable",
"     </a>",
"    </li>",
"    <li class=\"dashItem\">",
"     <a class=\"outlineLink\" href=\"#H8\">",
"      - Quarterly disposable",
"     </a>",
