var title_f17_50_18220="Suicide risk assessment2";
var content_f17_50_18220=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F67228&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F67228&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Suicide risk assessment for children and adolescents",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Factors related to intent (motivation)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        How strong is the intent (motivation)?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        If all the things you needed to kill yourself were available right here and you were alone, would you kill yourself right now?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Has the child or adolescent done anything to prepare?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Have you already written a note to your loved ones?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Have you practiced or come close already?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Factors related to emotional/behavioral regulation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        What is the level of hopelessness and helplessness? Despair?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Are intoxicants present?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Does the child or adolescent have any psychiatric disorders or comorbidities?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Does the child or adolescent have the capacity for self-regulation or is he or she impulsive?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Is the child or adolescent able to cope and solve problems?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Factors related to stressors",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Are there any recent or anticipated stressors?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Factors related to support systems",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Does the child or adolescent have a strong support system?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Among your family and friends, is there anyone who makes you feel less likely to kill yourself? More?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Can the child or adolescent build an alliance? Is he or she ready to participate in treatment?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        If I asked you to promise me or your family members to tell one of us when you were thinking about killing yourself, would you be able to keep that promise?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Are you willing to promise to tell one of us if you are feeling worse?",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Jacobs DG, Brewer M, Klein-Benheim M. Suicide assessment: An overview and recommended protocol. In: Harvard Medical School Guide to Assessment and Intervention in Suicide, Jacob D (Ed), Jossey-Bass, San Francisco 1998. p.3.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
