" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Assessment and management of intra-oral lacerations",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?17/51/18232/contributors\">",
