var title_f17_51_18236="Detailed criteria for the diagnosis of FMF";
var content_f17_51_18236=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F67666&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F67666&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Detailed criteria for the diagnosis of familial Mediterranean fever",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Major criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Typical attacks",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1. Peritonitis (generalized)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2. Pleuritis (unilateral) or pericarditis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3. Monoarthritis (hip, knee, ankle)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4. Fever alone",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Minor criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1-3.",
"        <strong>",
"         Incomplete",
"        </strong>",
"        attacks involving one or more of the following sites:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        1. Abdomen",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        2. Chest",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        3. Joint",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4. Exertional leg pain",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        5. Favorable response to colchicine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Supportive criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1. Family history of FMF",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2. Appropriate ethnic origin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3. Age &lt;20 years at disease onset",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        4-7. Features of attacks:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        4. Severe, requiring bed rest",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        5. Spontaneous remission",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        6. Symptom-free interval",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        7. Transient inflammatory response, with one or more abnormal test result(s) for the white blood cell count, erythrocyte sedimentation rate, serum amyloid A, and/or fibronogen",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        8. Episodic proteinuria/hematuria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        9. Negative laparotomy or removal of normal appendix",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        10. Consanguinity of parents",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Livneh A, Langevitz P, Zemer D, et al. Arthritis Rheum 1997; 40:1879.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
