var title_f17_52_18252="Grading staging NAFLD AGA";
var content_f17_52_18252=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F58613&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F58613&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Grading and staging of NAFLD",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        Grading NAFLD",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"2\">",
"        1. Macrovesicular steatosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\" colspan=\"2\">",
"        Grade 0: none",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\" colspan=\"2\">",
"        Grade 1: up to 33 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\" colspan=\"2\">",
"        Grade 2: 33 percent-66 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\" colspan=\"2\">",
"        Grade 3: &gt;66 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"2\">",
"        2. Necroinflammatory activity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Grade 1 (mild)",
"       </td>",
"       <td class=\"sublist_other\">",
"        Steatosis up to 66 percent, occasional ballooned hepatocyte (mainly zone 3), scattered intra-acinar neutrophils (PMN) &plusmn; lymphocytes, no or mild portal inflammation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Grade 2 (moderate)",
"       </td>",
"       <td class=\"sublist_other\">",
"        Steatosis of any degree, obvious zone III ballooning degeneration, intra-acinar PMNs, zone III perisinusoidal fibrosis may be present, mild to moderate, portal and intra-acinar inflammation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Grade 3 (severe)",
"       </td>",
"       <td class=\"sublist_other\">",
"        Panacinar steatosis, widespread ballooning, intra-acinar inflammation, PMNs associated with ballooned hepatocytes, mild to moderate portal inflammation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        Staging NAFLD",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1. Stage 1",
"       </td>",
"       <td>",
"        Zone III perisinusoidal/pericellular fibrosis; focally or extensively present",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2. Stage 2",
"       </td>",
"       <td>",
"        Zone III perisinusoidal/pericellular fibrosis with focal or extensive periportal fibrosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3. Stage 3",
"       </td>",
"       <td>",
"        Zone III perisinusoidal/pericellular fibrosis and portal fibrosis with focal or extensive bridging fibrosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4. Stage 4",
"       </td>",
"       <td>",
"        Cirrhosis",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from Brunt, EM, et al. Am J Gastroenterol 1999; 94:2467.",
"     <br>",
"      Reproduced with permission from: the American Gastroenterological Association. Sanyal, AJ. AGA technical review on nonalcoholic fatty liver disease. Gastroenterology 2002; 123:1705.",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
