" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    University of Washington Burn Center Pain Medication Guidelines",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Critical care treatment (without oral intake)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Critical care treatment (with oral intake)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Non-critical care treatment (large open areas)",
"       </td>",
"       <td class=\"subtitle1\">",
