var title_f17_57_18331="Criteria histrionic personality";
var content_f17_57_18331=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F50890&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F50890&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Criteria for histrionic personality disorder",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Five or more of following plus general criteria for personality disorder:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       1. Is uncomfortable in situations in which he or she is not the center of attention",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       2. Interaction with others is often characterized by inappropriate sexually seductive or provocative behavior",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       3. Displays rapidly shifting and shallow expression of emotion",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       4. Consistently uses physical appearance to draw attention to self",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       5. Style of speech that is excessively impressionistic and lacking in detail",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       6. Shows self-dramatization, theatricality, and exaggerated expression of emotion",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       7. Is suggestible, ie, easily influenced by others or circumstances",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       8. Considers relationships to be more intimate than they actually are",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from American Psychiatric Association. Diagnostic and Statistical Manual of Mental Disorders, 4th ed, Text revision (DSM-IV-TR). American Psychiatric Association, Washington, DC, 2000.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
