var title_f17_59_18364="Disadv main Rx prost CA";
var content_f17_59_18364=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F51287&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F51287&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    The disadvantages of the main treatment options for early prostate cancer",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        External beam radiation therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Significant risk of impotence",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Best outcomes require 3-D conformal treatments with doses above 72 Gy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Low risk of later rectal symptoms",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Knowledge of possible metastasis to lymph nodes is not available",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Up to half of patients have some temporary bladder or bowel symptoms during treatment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Brachytherapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Significant risk of impotence",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Successful cancer control is operator dependent, to some extent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Low risk of later rectal symptoms",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Knowledge of possible metastasis to lymph nodes is not available",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Treatment not appropriate for tumor outside the prostate capsule",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Up to half of patients have some temporary bladder or bowel symptoms with treatment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Radical prostatectomy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Significant risk of impotence",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Risk of operative morbidity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        The risk of complications is operator dependent, to some extent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Low risk of long-term incontinence",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Active observation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tumor may progress beyond the possibility for cure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Later treatment may result in more side effects",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Living with untreated cancer may cause anxiety",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Vogelzang NJ, Scardino PT, Shipley WU, et al. Comprehensive textbook of genitourinary oncology, 3rd Edition. Lippincott Williams &amp; Wilkins 2005. Copyright &copy; 2005 Lippincott Williams &amp; Wilkins.",
"    </div>",
"    <div class=\"contractual\">",
"     <br/>",
"     <a href=\"file://www.lww.com\">",
"      file://www.lww.com",
"     </a>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
