var title_f17_59_18365="Infection risk factors in lung transplant recipients";
var content_f17_59_18365=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F54898&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F54898&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Risk factors for infection in lung transplant recipients",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Recipient",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Underlying pathology (eg, diabetes/hepatitis) or other pathological history",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Age",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Absence of specific immunity to CMV, HSV, VZV, EBV",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Colonization of the recipient by bacteria or fungi",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Latent infection due to TB, CMV, VZV, HSV, EBV",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Previous therapy with antimicrobial agents, and glucocorticoids or other immunosuppressants",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Clinical state of the recipient at the time of transplantation: Renal failure, malnutrition, obesity, mechanical ventilation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Transplantation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Colonization of the graft by bacteria or fungi",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Preservation lesion, arterial ischemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Surgical factors:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Duration of procedure, surgical technique",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Surgical complications: Suture dehiscence, hemorrhage, arterial ischemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Repeat surgery required",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Postoperative instrumentation: Duration of mechanical ventilation, intravascular catheters, urethral catheter",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lung allograft is continuously exposed to the external environment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Denervation of allograft:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Diminished cough reflex",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Abnormal mucociliary clearance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Reactive hyperresponsiveness",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Interrupted lymphatic drainage (especially during first weeks)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Anastomosis site:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        May enhance colonization",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Airway dehiscence and mediastinitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bronchial stenosis and postobstructive infection",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Donor lung may transmit infections:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        From prolonged mechanical ventilation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        From latent infections (TB, CMV, VZV, HSV, EBV)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        From previous bacterial or fungal colonization",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Native lung after single-lung transplantation:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Occult pretransplant infection (TB, Aspergillus,",
"        <em>",
"         Pneumocystis jirovecii",
"        </em>",
"        , etc., especially with immunosuppression before transplantation)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sinus infection in cystic fibrosis and ciliary dysfunction syndromes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Bronchiolitis obliterans:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Enhanced immunosuppression",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Impaired clearance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Bronchiectasis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Immunosuppression",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Immunosuppressive agents: Cyclosporine, azathioprine, glucocorticoids, antilymphocyte antibodies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Immunomodulating viruses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Graft rejection",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CMV: cytomegalovirus; HSV: herpes simplex virus; VZV: varicella zoster virus; EBV: Epstein-Barr virus; TB: tuberculosis.",
"    </div>",
"    <div class=\"reference\">",
"     Modified with permission from: Gavalda J, Roman A, Pahissa A, et al. Risks and epidemiology of infections after lung or heart-lung transplantation. In: Transplant Infections, 3rd ed, Bowden RA, Ljungman P, Snydman DR (Eds), Lippincott Williams &amp; Wilkins, 2010. Copyright &copy; 2010 Lippincott Williams &amp; Wilkins.",
"    </div>",
"    <div class=\"contractual\">",
"     <br/>",
"     <a href=\"file://www.lww.com\">",
"      file://www.lww.com",
"     </a>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
