var title_f17_6_17516="Causes eosinophilic meningitis";
var content_f17_6_17516=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F68987&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F68987&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Causes of eosinophilic meningitis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Infectious, parasitic causes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Roundworm (nematode) infections - commonly present as eosinophilic meningitis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Angiostrongylus cantonensis - migrating larvae inherently neurotropic",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Gnathostoma spinigerum - migrating larvae in visceral and/or neural tissues",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Baylisascaris procyonis - migrating larvae inherently neurotropic",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Tapeworm (cestode) infections - may present as eosinophilic meningitis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Cysticercosis - cysts develop in CNS and/or visceral tissues",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Fluke (trematode) infections - occasionally cause eosinophilic meningitis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Paragonimus westermani - ectopic spinal or cerebral localization",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Schistosomiasis - ectopic spinal or cerebral localization",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Fascioliasis - ectopic CNS localization",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Other roundworm infections which occasionally cause eosinophilic meningitis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Toxocariasis - migrating larvae",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Nonparasitic, infectious causes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Coccidioidomycosis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Cryptococcosis - CSF eosinophilia rare",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Myiasis - with CNS penetration",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Virus and bacteria - are of uncertain causality",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Noninfectious causes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Idiopathic hypereosinophilic syndromes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Ventriculoperitoneal shunts",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Leukemia or lymphoma with CNS involvement (Hodgkin's)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Nonsteroidal antiinflammatory drugs",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Antibiotics - ciprofloxacin, trimethoprim-sulfamethoxazole, intraventricular gentamicin or vancomycin",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Myelography contrast agents",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Weller, PF, Liu, LX, Semin Neurol 1993; 13:161.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
