var title_f17_60_18381="Psychodynamic defense mechanisms";
var content_f17_60_18381=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PSYCH%2F81648&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PSYCH%2F81648&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Psychodynamic defense mechanisms",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Defense mechanisms",
"       </td>",
"       <td class=\"subtitle1\">",
"        Description",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Primitive defenses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Splitting",
"       </td>",
"       <td>",
"        Compartmentalizing internal representations of self and others so that conflict is avoided and integration of these representations is not possible",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Projective identification",
"       </td>",
"       <td>",
"        A defense involving subtle interpersonal pressure so that the target of a projection takes on characteristics of either an aspect of the self or the internal object that is being projected",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Projection",
"       </td>",
"       <td>",
"        Externalizing unacceptable inner impulses and their derivatives by attributing them to someone else",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Denial",
"       </td>",
"       <td>",
"        Avoiding awareness of aspects of external realities that are difficult to face by dismissing perceptions that are obvious to everyone else in the environment. What is not perceived is not real, and thus cannot cause pain. The unpleasant aspects that are denied may be replaced by a more pleasant internal fantasy.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Disavowal",
"       </td>",
"       <td>",
"        Dismissing or failing to acknowledge thoughts or feelings that are uncomfortable",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Schizoid fantasy",
"       </td>",
"       <td>",
"        Retreating into one's private, internal world to avoid anxiety about interpersonal situations",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Regression",
"       </td>",
"       <td>",
"        A partial or total return to earlier patterns of adaptation or behavior",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Conversion",
"       </td>",
"       <td>",
"        Development of symbolic physical symptoms and distortions involving the voluntary muscles or special sense organs; symptoms are not under voluntary control and cannot be explained by any physical disorder",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Undoing",
"       </td>",
"       <td>",
"        Symbolically acting out in reverse something unacceptable that has already been done or against an impulse which the ego must defend itself",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Higher-level defenses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Rationalization",
"       </td>",
"       <td>",
"        Justifying irrational or unacceptable behavior, motives, or feelings by making them appear rational and reasonable through belief in plausible explanations that are false",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Displacement",
"       </td>",
"       <td>",
"        Shifting unacceptable feelings associated with one idea or object to another that resembles the original in some way",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Intellectualization",
"       </td>",
"       <td>",
"        Using excessive and abstract rational and logical reasoning to avoid difficult feelings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Isolation of affect",
"       </td>",
"       <td>",
"        Separating an idea or impulse from its associated feeling or affect to avoid emotional turmoil",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Sexualization",
"       </td>",
"       <td>",
"        Endowing an object or behavior with sexual significance to turn a negative experience into an exciting and stimulating one",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Reaction formation",
"       </td>",
"       <td>",
"        Transforming an unacceptable wish or impulse into its opposite",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Sublimation",
"       </td>",
"       <td>",
"        Energy associated with unacceptable impulses or wishes is diverted into personally and socially acceptable channels",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Humor",
"       </td>",
"       <td>",
"        Finding comic and/or ironic elements in difficult situations to reduce unpleasant affect and personal discomfort",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Anticipation",
"       </td>",
"       <td>",
"        Committing oneself to the needs of others over and above one's own needs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Suppression",
"       </td>",
"       <td>",
"        Consciously deciding not to attend to a particular feeling, state, or impulse",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Repression",
"       </td>",
"       <td>",
"        Expelling unacceptable ideas or impulses, thus blocking them from entering consciousness (repression is more closely linked with inner states, whereas denial involves external sensory data)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Gabbard, GO. Long-Term Psychodynamic Psychotherapy: a Basic Text, 2nd ed, American Psychiatric Publishing Arlington, VA 2007.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
