var title_f17_7_17532="Burn depth";
var content_f17_7_17532=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F57124&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F57124&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Burn depth",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Depth",
"      </td>",
"      <td class=\"subtitle1\">",
"       Cause",
"      </td>",
"      <td class=\"subtitle1\">",
"       Appearance",
"      </td>",
"      <td class=\"subtitle1\">",
"       Sensation",
"      </td>",
"      <td class=\"subtitle1\">",
"       Healing time",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Superficial",
"      </td>",
"      <td>",
"       <p>",
"        Ultraviolet exposure",
"       </p>",
"       <p>",
"        Very short flash",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        Dry, red",
"       </p>",
"       <p>",
"        Blanches with pressure",
"       </p>",
"      </td>",
"      <td>",
"       Painful",
"      </td>",
"      <td>",
"       3 to 6 days",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Superficial partial-thickness",
"      </td>",
"      <td>",
"       <p>",
"        Scald (spill or splash)",
"       </p>",
"       <p>",
"        Short flash",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        Blisters",
"       </p>",
"       <p>",
"        Moist, red, weeping",
"       </p>",
"       <p>",
"        Blanches with pressure",
"       </p>",
"      </td>",
"      <td>",
"       Painful to temperature and air",
"      </td>",
"      <td>",
"       7 to 20 days",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Deep partial-thickness",
"      </td>",
"      <td>",
"       <p>",
"        Scald (spill)",
"       </p>",
"       <p>",
"        Flame",
"       </p>",
"       <p>",
"        Oil",
"       </p>",
"       <p>",
"        Grease",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        Blisters (easily unroofed)",
"       </p>",
"       <p>",
"        Wet or waxy dry",
"       </p>",
"       <p>",
"        Variable color (patchy to cheesy white to red)",
"       </p>",
"       <p>",
"        Does not blanch with pressure",
"       </p>",
"      </td>",
"      <td>",
"       Perceptive of pressure only",
"      </td>",
"      <td>",
"       &gt;21 days",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Full-thickness",
"      </td>",
"      <td>",
"       <p>",
"        Scald (immersion)",
"       </p>",
"       <p>",
"        Flame",
"       </p>",
"       <p>",
"        Steam",
"       </p>",
"       <p>",
"        Oil",
"       </p>",
"       <p>",
"        Grease",
"       </p>",
"       <p>",
"        Chemical",
"       </p>",
"       <p>",
"        Electrical",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        Waxy white to leathery gray to charred and black",
"       </p>",
"       <p>",
"        Dry and inelastic",
"       </p>",
"       <p>",
"        No blanching with pressure",
"       </p>",
"      </td>",
"      <td>",
"       Deep pressure only",
"      </td>",
"      <td>",
"       Never (if &gt;2 percent total body surface area)",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Mertens, DM, Jenkins, ME, Warden, GD, Med Clin North Am 1997; 32:343; and Peate, WF, Am Fam Physician 1992; 45:1321; and Clayton, MC, Solem, LD, Postgrad Med 1995; 97:151.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
