var content_f18_0_18444=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F60091&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F60091&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Parenteral regimens for empiric treatment of complicated pyelonephritis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Antimicrobial agent&nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Dose, interval",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Mild to moderate pyelonephritis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ceftriaxone",
"       </td>",
"       <td>",
"        1 g every 24 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cefepime",
"       </td>",
"       <td>",
"        1 g every 12 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ciprofloxacin",
"       </td>",
"       <td>",
"        400 mg every 12 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Levofloxacin",
"       </td>",
"       <td>",
"        750 mg every 24 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Aztreonam*",
"       </td>",
"       <td>",
"        1 g every 8 to 12 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Severe pyelonephritis with immunocompromise and/or incomplete urinary drainage",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ampicillin-sulbactam",
"       </td>",
"       <td>",
"        1.5 g every 6 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ticarcillin-clavulanate",
"       </td>",
"       <td>",
"        3.1 g every 6 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Piperacillin-tazobactam",
"       </td>",
"       <td>",
"        3.375 g every 6 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Meropenem",
"       </td>",
"       <td>",
"        500 mg every 8 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Imipenem",
"       </td>",
"       <td>",
"        500 mg every 6 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Doripenem&nbsp;",
"       </td>",
"       <td>",
"        500 mg every 8 hours&nbsp;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Previous antimicrobial use and results of any recent urine cultures should inform the choice of an empiric regimen.",
"    <br>",
"     Doses are for patients with normal renal function.",
"     <br>",
"      In the setting of pregnancy the above agents are acceptable with the exceptions of ciprofloxacin, levofloxacin, and imipenem. The treatment of urinary tract infection due to enterococcus is discussed separately.",
"      <br>",
"       If methicillin-resistant S. aureus (MRSA) is known or suspected, see treatment regimens outlined separately in topics addressing MRSA management.",
"       <div class=\"footnotes\">",
"        * Alternative in the setting of beta lactam allergy.",
"       </div>",
"       <div class=\"reference\">",
"       </div>",
"      </br>",
"     </br>",
"    </br>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
var script_f18_0_18444=[""].join("\n");
