var title_f18_0_18445="Low sodium diet B";
var content_f18_0_18445=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F63387&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F63387&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Low-sodium diet (continued)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Foods to choose",
"      </td>",
"      <td class=\"subtitle1\">",
"       Foods to avoid",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\">",
"       Breads:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Whole-grain breads, English muffins, bagels",
"      </td>",
"      <td>",
"       Biscuits, prepared mixes (pancake, muffin, cornbread)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\">",
"       Cereals:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Cooked hot cereals (not instant), such as oatmeal, cream of wheat, rice, or farina; puffed wheat; puffed rice; shredded wheat",
"      </td>",
"      <td>",
"       Instant hot cereals, many boxed cold cereals",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\">",
"       Crackers and snack foods:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       All unsalted crackers and snack foods, unsalted peanut butter, unsalted nuts or seeds",
"      </td>",
"      <td>",
"       Salted crackers and snack items (chips, pretzels, popcorn), regular peanut butter, prepared dips/spreads, salted nuts or seeds",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\">",
"       Pasta, rice, and potatoes:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Any type of pasta; potatoes; white or brown rice",
"      </td>",
"      <td>",
"       Macaroni and cheese mix; rice, noodle, or spaghetti mixes; canned spaghetti; frozen lasagna; instant potatoes; seasoned potato mixes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\">",
"       Dried beans and peas:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Any dried beans or peas without seasoning",
"      </td>",
"      <td>",
"       Beans or peas prepared with ham, bacon, salt pork, or bacon grease; all canned beans",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\">",
"       Meats and alternatives:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Fresh or frozen beef, poultry, and fish; low-sodium canned tuna and salmon; eggs",
"      </td>",
"      <td>",
"       Salted, smoked, canned, spiced, and cured meat, poultry, or fish; bacon; ham; sausage; lunch meats; hot dogs; breaded frozen meat, fish, or poultry; frozen dinners; pizza",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\">",
"       Fruits and vegetables:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Any fresh, frozen, or canned fruit, any fresh or frozen vegetables without sauce, canned vegetables without salt, low-salt tomato sauce/paste",
"      </td>",
"      <td>",
"       Regular canned vegetables and vegetable juices, regular tomato sauce and tomato paste, olives, pickles, relishes, sauerkraut, frozen vegetables in butter or sauces, crystallized and glazed fruit, maraschino cherries, fruit dried with sodium sulfite",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\">",
"       Dairy products:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Milk, cream, sour cream, non-dairy creamer, yogurt, low-sodium cottage cheese, low-sodium cheese",
"      </td>",
"      <td>",
"       Buttermilk, Dutch processed chocolate milk, processed cheese slices and spreads, processed cheese, cottage cheese, aged or natural cheese",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\">",
"       Fats and oils:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Plant oils (olive, canola, corn, peanut), unsalted butter or margarine",
"      </td>",
"      <td>",
"       Prepared salad dressings, bacon, salt pork, fat back, salted butter or margarine",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\">",
"       Soups:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Salt-free soups and low-sodium bouillon cubes, unsalted broth, homemade soup without added salt",
"      </td>",
"      <td>",
"       Regular canned or prepared soups, stews, broths, or bouillon; packaged and frozen soups",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\">",
"       Desserts:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Gelatin, sherbet, pudding, ice cream, salt-free baked goods, sugar, honey, jam, jelly, marmalade, syrup",
"      </td>",
"      <td>",
"       Packaged baked goods",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\">",
"       Beverages:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Coffee, tea, soft drinks, fruit-flavored drinks, low-salt tomato juice, any fruit juice",
"      </td>",
"      <td>",
"       Softened water; carbonated beverages with sodium or salt added; regular tomato juice (V-8); ask about alcoholic beverages",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\">",
"       Condiments:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Fresh and dried herbs; lemon juice; low-salt mustard, vinegar, Tabasco sauce; low- or no-salt ketchup; seasoning blends that do not contain salt",
"      </td>",
"      <td>",
"       Table salt, lite salt, bouillon cubes, meat extract, taco seasoning, Worcestershire sauce, tartar sauce, ketchup, chili sauce, cooking sherry and wine, onion salt, mustard, garlic salt, soy sauce, tamari, meat flavoring or tenderizer, steak and barbecue sauce, seasoned salt, monosodium glutamate (MSG), Dutch processed cocoa",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
