var title_f18_1_18461="Age-specific diagnostic priorities for children with ALF";
var content_f18_1_18461=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F83772&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F83772&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Age-specific diagnostic priorities for children with acute liver failure",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        &lt;3 months of age",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Herpes blood PCR (or other testing: HSV IgM, viral culture of blood or CSF, CSF PCR)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Enterovirus blood PCR (or other testing)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lactate, pyruvate (mitochondrial screen)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Plasma acylcarnitine profile (fatty acid oxidation defects)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ferritin (screen for gestational alloimmune liver disease [neonatal hemachromatosis])",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Serum amino acid profile (urea cycle and metabolic)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Echocardiogram: Cardiac dysfunction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Abdominal ultrasound with Doppler (vascular and anatomic)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Confirm newborn screening results (galactosemia and tyrosinemia)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Confirm maternal hepatitis B serology",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        3 months to 4 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        HBsAg, HAV IgM, EBV VCA IgM or EBV PCR",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lactate, pyruvate (mitochondrial screen)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Autoimmune markers: ANA, ASMA, ALKM, IgG",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Drug history, acetaminophen level",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Plasma acylcarnitine profile (fatty acid oxidation defects)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Serum amino acids",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Abdominal ultrasound with Doppler (vascular and anatomic)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        5 years to 18 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        HBsAg, HAV IgM, EBV (EBV VCA IgM or PCR)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Autoimmune markers: ANA, ASMA, ALKM, IgG",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ceruloplasmin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Drug history, acetaminophen level",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lactate, pyruvate (screen for mitochondrial disorders)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Plasma acylcarnitine profile (Fatty acid oxidation defects)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Serum amino acids",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Abdominal ultrasound with Doppler (vascular and anatomic)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Additional diagnostic screening tests to consider, directed by history and clinical course",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Infectious causes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Blood culture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Viral PCR in blood for adenovirus, enterovirus, EBV, HHV-6, parvovirus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Nasal wash for influenza (infections)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Hepatitis E antibody",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Serologic tests for celiac disease&nbsp;(eg, tissue transglutaminase)&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Soluble IL2R, ferritin in older patients, triglycerides (hemophagocytic lymphohistiocytosis)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Echocardiogram (cardiac)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        MRI for tissue iron (gestational alloimmune liver disease [neonatal hemochromatosis])",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Urine succinyl acetone (tyrosinemia)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Urine orotic acid (urea cycle defects)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Urine organic acids (metabolic)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Liver copper, Wilson gene mutation analysis (Wilson)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Liver biopsy for histology, culture, electron microscopy",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     PCR: polymerase chain reaction; HSV: herpes simplex virus; IgM: immunoglobulin M; CSF: cerebrospinal fluid; HBsAg: hepatitis B s antigen; HAV: hepatitis A virus; EBV: Epstein-Barr virus; VCA: viral capsid antigen; ANA: anti-nuclear antibodies; ASMA: anti-smooth muscle antibodies; ALKM: anti-liver-kidney microsomal antibodies; HHV-6: human herpes virus 6; IL2R: interleukin 2 receptor. MRI: magnetic resonance imaging.",
"    </div>",
"    <div class=\"reference\">",
"     Modified with permission from: Squires RH, Alonso EM. Acute liver failure in children. In: Liver Disease in Children, 4th ed, Suchy FJ, Sokol RJ, Balistreri WF (Eds), Cambridge University Press, New York 2012. Copyright &copy; 2012 Cambridge University Press.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
