var title_f18_12_18635="Tendinopathy phases";
var content_f18_12_18635=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F56664&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F56664&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Tendinosis phases of pain",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Phase I",
"       </td>",
"       <td>",
"        Mild pain after exercise activity, resolves within 24 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Phase II",
"       </td>",
"       <td>",
"        Pain after exercise activity, exceeds 48 hours, resolves with warm-up",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Phase III",
"       </td>",
"       <td>",
"        Pain with exercise activity that does not alter activity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Phase IV",
"       </td>",
"       <td>",
"        Pain with exercise activity that alters activity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Phase V",
"       </td>",
"       <td>",
"        Pain caused by heavy activities of daily living",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Phase VI",
"       </td>",
"       <td>",
"        <p>",
"         Intermittent pain at rest that does not disturb sleep",
"        </p>",
"        <p>",
"         Pain caused by light activities of daily living",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Phase VII",
"       </td>",
"       <td>",
"        Constant rest pain (dull aching) and pain that disturbs sleep",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Nirschl, RP. Elbow tendinosis/Tennis elbow. Clinics in sports medicine 1992; 11:851. Copyright &copy;1992 Elsevier.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
