var title_f18_12_18636="Immunosuppressants and skin cancer";
var content_f18_12_18636=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=DERM%2F62170&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=DERM%2F62170&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Immunosuppressive agents and nonmelanoma skin cancer risk",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Drug name",
"       </td>",
"       <td class=\"subtitle1\">",
"        Mechanism",
"       </td>",
"       <td class=\"subtitle1\">",
"        Risk of NMSC",
"       </td>",
"       <td class=\"subtitle1\">",
"        Dermatological side effects",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Systemic glucocorticoids",
"       </td>",
"       <td>",
"        Inhibit antigen presentation, induce lymphocyte toxicity, alter cytokine production",
"       </td>",
"       <td>",
"        +",
"       </td>",
"       <td>",
"        Acne, striae, skin fragility, ecchymosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Azathioprine",
"       </td>",
"       <td>",
"        Inhibits purine synthesis",
"       </td>",
"       <td>",
"        ++++",
"       </td>",
"       <td>",
"        Hypersensitivity reaction including urticaria, maculopapular and vasculitic eruptions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mycophenolate mofetil",
"       </td>",
"       <td>",
"        Inhibits guanine nucleotide synthesis",
"       </td>",
"       <td>",
"        +++",
"       </td>",
"       <td>",
"        Non-specific rash",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cyclosporine",
"       </td>",
"       <td>",
"        <p>",
"         Calcineurin inhibitor",
"        </p>",
"        <p>",
"         Decreases T cell activation and IL-2 production",
"        </p>",
"       </td>",
"       <td>",
"        ++++",
"       </td>",
"       <td>",
"        Hirsutism, sebaceous hyperplasia, gingival hyperplasia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tacrolimus",
"       </td>",
"       <td>",
"        Calcineurin inhibitor",
"       </td>",
"       <td>",
"        +++",
"       </td>",
"       <td>",
"        Alopecia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sirolimus",
"       </td>",
"       <td>",
"        <p>",
"         Inhibits mammalian target of rapamycin",
"        </p>",
"        <p>",
"         Abrogates IL-2 signal transduction",
"        </p>",
"       </td>",
"       <td>",
"        + (Anti-carcinogenic effect in limited studies)",
"       </td>",
"       <td>",
"        Impaired wound healing, acne/folliculitis, edema",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Everolimus",
"       </td>",
"       <td>",
"        <p>",
"         Inhibits mammalian target of rapamycin",
"        </p>",
"        <p>",
"         Abrogates IL-2 signal transduction",
"        </p>",
"       </td>",
"       <td>",
"        + (No long term data but probably similar to sirolimus)",
"       </td>",
"       <td>",
"        Mouth ulcers, stomatitis",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Original table modified for this publication. From: Rigel DS, Robinson JK, Ross MI, et al (Eds). Cancer of the Skin, 2nd Edition. Philadelphia: Elsevier, 2011. Table used with the permission of Elsevier Inc. All rights reserved.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
