var title_f18_16_18699="Annual HIV and syphilis screening for adolescents";
var content_f18_16_18699=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F73710&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F73710&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Criteria for annual human immunodeficiency virus and syphilis screening in adolescents",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        History of sexually transmitted diseases, or patients seeking care for STD",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Multiple sexual partners",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        More than one sexual partner in the last six months, or since last HIV test",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Exchange of sex for drugs or money by patient or a sexual partner",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Injection drug use by patient or&nbsp;a sexual partner",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Admittance to jail or other detention facility",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Residence in areas with high prevalence of HIV and syphilis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        For males: Sex with males",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sex partner with known or suspected HIV",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     STD: sexually transmitted disease; HIV: human immunodeficiency virus.",
"    </div>",
"    <div class=\"reference\">",
"     <p>",
"      Adapted from Branson BM, Handsfield HH, Lampe MA, et al. Revised recommendations for HIV testing of adults, adolescents, and pregnant women in health-care settings. MMWR 2006; 55(RR14):1.",
"     </p>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
