var script_f18_2_18466=[" ",
"",
"",
"function log(i){",
"return Math.log(i) * Math.LOG10E;   ",
"}",
"",
"function ln(i){",
"return Math.log(i);",
"}",
"",
"function sq(i){",
"return i * i;",
"}",
"",
"function sqr(i){",
"return Math.sqrt(i);",
"}",
"",
"",
"function power(x,y){",
"return Math.pow(x,y);",
"}",
"",
"function eTo(x){",
"return Math.exp(x);",
"}",
"",
"",
"function fixDP(r, dps) {",
"if (isNaN(r)) return \"NaN\";",
"var msign = '';",
"if (r < 0) msign = '-';",
"x = Math.abs(r);",
"if (x > Math.pow(10, 21)) return msign + x.toString();",
"var m = Math.round(x * Math.pow(10, dps)).toString();",
"if (dps == 0) return msign + m;",
"while (m.length <= dps) m = \"0\" + m;",
"return msign + m.substring(0, m.length - dps) + \".\" + m.substring(m.length - dps);",
"}",
"",
"var currenttimeout;",
"",
"",
"var calctxt = ''; ",
"var xmltxt = ''; ",
"var htmtxt = ''; ",
"",
"",
"",
"function MELD_SI_fx() {",
"",
"currenttimeout = self.setTimeout('minMaxCheck();', 3000);",
"",
"with(document.MELD_SI_form){",
"",
"",
"doCalc = true;",
"if (Age_param.value.indexOf(',') >= 0){ Age_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Age_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Age_unit.options[Age_unit.selectedIndex].value.split('|');",
"Age = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (Creatinine_param.value.indexOf(',') >= 0){ Creatinine_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Creatinine_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Creatinine_unit.options[Creatinine_unit.selectedIndex].value.split('|');",
"Creatinine = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (Bilirubin_param.value.indexOf(',') >= 0){ Bilirubin_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Bilirubin_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Bilirubin_unit.options[Bilirubin_unit.selectedIndex].value.split('|');",
"Bilirubin = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (INR_param.value.indexOf(',') >= 0){ INR_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(INR_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"INR = param_value;",
"if (Exception_Diagnoses_Age_12_to_17_Years_radio[0].checked){ Exception_Diagnoses_Age_12_to_17_Years = 0; }",
"if (Exception_Diagnoses_Age_12_to_17_Years_radio[1].checked){ Exception_Diagnoses_Age_12_to_17_Years = 1; }",
"if (Exception_Diagnoses_Age_12_to_17_Years_radio[2].checked){ Exception_Diagnoses_Age_12_to_17_Years = 1; }",
"if (Exception_Diagnoses_Age_12_to_17_Years_radio[3].checked){ Exception_Diagnoses_Age_12_to_17_Years = 1; }",
"dp = 0;",
"if (Hemodialysis_twice_in_week_prior_radio[0].checked){",
"Creatinine_param.value = '353.6'; Creatinine = 353.6;",
"for(i = 0; i < Creatinine_unit.options.length; i++){",
"if (Creatinine_unit.options[i].value == '1|0|mcmol/L') Creatinine_unit.selectedIndex = i;",
"}}",
"MELD_Score =  10 * ((0.957 * ln(Creatinine/88.4)) + (0.378 * ln(Bilirubin/17.1)) + (1.12 * ln(INR))) + 6.43;",
"",
"if (doCalc) MELD_Score_param.value = fixDP(MELD_Score, dp);",
"",
"",
"",
"if (Exception_Diagnoses_Age_12_to_17_Years && (Age >= 12) && (Age <= 17)){",
"alert('Beacuse of this specific disease, the MELD Score is set at 30');",
"MELD_Score_param.value = '30';",
"}",
"",
"",
"}",
"",
"}",
"",
"function minMaxCheck(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.MELD_SI_form){",
"",
"if (Age_param.value && Age < 12) {",
"Age = 0;",
"Age_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for Age is 12 yr.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Creatinine_param.value && Creatinine < 88.4) {",
"Creatinine = 0;",
"Creatinine_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for Creatinine is 88.4 mcmol/L.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Creatinine_param.value && Creatinine > 353.6) {",
"Creatinine_param.value = \"\";",
"clrResults();",
"Creatinine = 0;",
"doCalc = false;",
"alert(\"The maximum value for Creatinine is 353.6 mcmol/L.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Bilirubin_param.value && Bilirubin < 17) {",
"Bilirubin = 0;",
"Bilirubin_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for Bilirubin is 17 mcmol/L.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (INR_param.value && INR < 1) {",
"INR = 0;",
"INR_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for INR is 1 .\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (INR_param.value && INR > 100) {",
"INR_param.value = \"\";",
"clrResults();",
"INR = 0;",
"doCalc = false;",
"alert(\"The maximum value for INR is 100 .\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"",
"",
"}",
"",
"}",
"",
"",
"function clrResults(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.MELD_SI_form){",
"",
"MELD_Score_param.value = '';",
"",
"",
"}",
"",
"}",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
""].join("\n");
