var title_f18_20_18763="Early signs of autism";
var content_f18_20_18763=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F74400&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F74400&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Early signs of autism",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Gaze",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Lack of appropriate gaze",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Lack of warm, joyful expressions with gaze",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Receptive language",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Lack of recognition of mother's, father's, or consistent caregiver's voice",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Lack of response to name",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Increased awareness of environmental sounds",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Lack of interest in or response to comments made by others",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Expressive language",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Lack of to-and-fro pattern of vocalization that typically occurs by about 6 months of age",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Delayed onset of babbling (after 9 months of age)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Lack of expressions such as \"oh-oh\" or \"huh\"",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Pre-speech gestures",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Decreased or absent use of pre-speech gestures, such as waving, pointing, showing",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Johnson CP, Myers SM. Identification and evaluation of children with autism spectrum disorders. Pediatrics 2007; 120:1183.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
