var title_f18_22_18795="Utility of cardiac MRI";
var content_f18_22_18795=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F79652&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F79652&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Advantages and disadvantages of cardiac MRI",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Advantages",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Three-dimensional",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        High spatial and temporal resolution",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Intrinsic high contrast; no need for iodinated contrast",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        No ionizing radiation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        No interference from lung or bone",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Multiple imaging techniques in a single system",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Disadvantages",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Contraindicated with certain medical implants such as aneurysm clips, TENS units, and certain pacemakers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Acquisition time can be lengthy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Electrocardiogram can be distorted by the magnetic field interfering with monitoring of acutely ill patients",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Requires ECG and respiratory gating",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Claustrophobia",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
