var title_f18_23_18812="Higher cortical function in children";
var content_f18_23_18812=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F78835&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F78835&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Assessment of higher cortical function in children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Age",
"       </td>",
"       <td class=\"subtitle1\">",
"        Evidence of normal cortical function",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        6 to 12 months",
"       </td>",
"       <td>",
"        Awareness of surroundings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Interaction with examiner (social smile, inquisitiveness, habituation)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cooing and gurgling, sometimes making of nonspecific \"mama\" and \"dada\" sounds",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"3\">",
"        12 to 20 months",
"       </td>",
"       <td>",
"        Six to eight word vocabulary",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Comprehends one-step commands",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Points to two or three body parts",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        24 months",
"       </td>",
"       <td>",
"        Names two or three body parts",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Uses phrases and simple sentences",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        24 to 36 months",
"       </td>",
"       <td>",
"        Concept of self (referring to self as \"I\", knowledge of name and age)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"4\">",
"        36 months",
"       </td>",
"       <td>",
"        Counts three objects",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Understands prepositional concepts (eg, \"over\" and \"under\")",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Asks questions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Names three colors",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        48 months",
"       </td>",
"       <td>",
"        Copies a square and a cross",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        5 or 6 years",
"       </td>",
"       <td>",
"        Spells monosyllabic words",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Counts to 10",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        6 years",
"       </td>",
"       <td>",
"        Copies a triangle",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        6 or 7 years",
"       </td>",
"       <td>",
"        Does simple addition and subtraction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Reads polysyllabic words",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        7 years",
"       </td>",
"       <td>",
"        Copies a diamond",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
var script_f18_23_18812=[""].join("\n");
