var script_f18_26_18849=[" ",
"",
"var currenttimeout;",
"",
"function PediatricETTubeSize_fx() {",
"",
"currenttimeout = self.setTimeout('minMaxCheck();', 3000);",
"",
"with(document.PediatricETTubeSize_form){",
"",
"doCalc = true;",
"",
"if (Age_param.value.indexOf(',') >= 0){ Age_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"",
"param_value = parseFloat(Age_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Age_unit.options[Age_unit.selectedIndex].value.split('|');",
"Age = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"",
"if ((Age - Math.floor(Age)) >= 0.5){ Round_Age = Math.floor(Age) + 1; } else { Round_Age = Math.floor(Age); };",
"",
"if (doCalc){",
"",
"if (Round_Age == 1){ Uncuffed_Endotracheal_Tube_Size_param.value = '4.0'; }; ",
"if (Round_Age == 2){ Uncuffed_Endotracheal_Tube_Size_param.value = '4.5'; Cuffed_Endotracheal_Tube_Size_param.value = '4.0'; }; ",
"if (Age < 2){ Cuffed_Endotracheal_Tube_Size_param.value = '3.5'; }; ",
"if (Round_Age == 3){ Uncuffed_Endotracheal_Tube_Size_param.value = '5.0'; Cuffed_Endotracheal_Tube_Size_param.value = '4.0'; }; ",
"if (Round_Age == 4){ Uncuffed_Endotracheal_Tube_Size_param.value = '5.0'; Cuffed_Endotracheal_Tube_Size_param.value = '4.5'; }; ",
"if (Round_Age == 5){ Uncuffed_Endotracheal_Tube_Size_param.value = '5.0'; Cuffed_Endotracheal_Tube_Size_param.value = '4.5'; }; ",
"if (Round_Age == 6){ Uncuffed_Endotracheal_Tube_Size_param.value = '5.5'; Cuffed_Endotracheal_Tube_Size_param.value = '5.0'; }; ",
"if (Round_Age == 7){ Uncuffed_Endotracheal_Tube_Size_param.value = '6.0'; Cuffed_Endotracheal_Tube_Size_param.value = '5.0'; }; ",
"if (Round_Age == 8){ Uncuffed_Endotracheal_Tube_Size_param.value = '6.0'; Cuffed_Endotracheal_Tube_Size_param.value = '5.5'; }; ",
"",
"}",
"}}",
"",
"",
"function minMaxCheck(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.PediatricETTubeSize_form){",
"",
"if (Age_param.value && Age < 1) {",
"Age = 0;",
"Age_param.value = '';",
"clrResults();",
"doCalc = false;",
"alert('The minimum value for Age is 1 yr.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.');",
"}",
"",
"if (Age_param.value && Age > 8) {",
"Age_param.value = '';",
"clrResults();",
"Age = 0;",
"doCalc = false;",
"alert('The maximum value for Age is 8 yr.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.');",
"}",
"",
"}}",
"",
"",
"function clrResults(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.PediatricETTubeSize_form){",
"",
"Uncuffed_Endotracheal_Tube_Size_param.value = '';",
"Cuffed_Endotracheal_Tube_Size_param.value = '';",
"",
"}}",
"",
""].join("\n");
