var title_f18_26_18860="Mechanical vent in BP fistula";
var content_f18_26_18860=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F66471&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F66471&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Guidelines for ventilator management in the patient with bronchopleural fistula",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Reduce both mean airway pressure and the number of",
"positive-pressure breaths, using the lowest number of mechanical",
"breaths that permits acceptable alveolar ventilation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Wean patient completely if possible&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Partial ventilatory support (eg, low-rate SIMV or pressure support) is preferable to total ventilatory support (eg, assist/control, high-rate SIMV, or pressure control ventilation)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Avoid or correct respiratory alkalosis (to minimize minute ventilation)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Consider use of permissive hypercapnia (reducing minute ventilation and allowing arterial PCO2 to rise)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Limit effective (returned) tidal volume to 5 to 8 mL/kg",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Minimize inspiratory time, and hence mean airway pressure:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Keep inspiration-to-expiration ratio low (eg, 0.33)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Use high inspiratory flow rate (eg, 70 to 100 L/min)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Avoid inflation hold (end-inspiratory pause) and inverse-ratio ventilation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Use low-compressible-volume (non-disposable) ventilator circuit",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Minimize PEEP (both dialed-in and auto-PEEP)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Use least amount of chest tube suction that maintains lung inflation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        If spontaneous movement exacerbates leak, keep patient",
"heavily sedated (in unusual circumstances muscle relaxation may also be",
"necessary)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Explore position differences, and avoid patient positions that increase the leak",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Treat bronchospasm and other causes of expiratory airflow obstruction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Consider specific or unconventional measures (eg, independent",
"lung ventilation, high-frequency jet ventilation, PEEP to chest tubes,",
"etc) only if the air leak per se is clinically felt to be worsening the",
"patient's condition (eg, intractable hypotension or arrhythmias in",
"association with respiratory acidosis)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Treat underlying cause of respiratory failure, maintaining",
"nutritional and other support, with goal of discontinuing mechanical",
"ventilation as soon as possible",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
