var title_f18_26_18861="Sources of iodine";
var content_f18_26_18861=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ENDO%2F60901&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ENDO%2F60901&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Iodine content of some iodine-containing medications and radiographic contrast agents",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Substance",
"       </td>",
"       <td class=\"subtitle1\">",
"        Amount of iodine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Potassium iodides (thyroprotectant and prethyroidectomy agents)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Potassium iodide oral tablet",
"       </td>",
"       <td>",
"        50 mg iodine per 65 mg potassium iodide tablet; 100 mg iodine per 130 mg potassium iodide tablet",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Potassium iodide oral solution",
"       </td>",
"       <td>",
"        50 mg iodine per mL of 65 mg/mL potassium iodide solution",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Potassium iodide oral saturated solution (SSKI)",
"       </td>",
"       <td>",
"        770 mg iodine per mL (&asymp;20 drops) of 1000 mg/mL potassium iodide saturated solution",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Potassium iodide and iodine mixture (Lugol solution, Strong iodine solution)",
"       </td>",
"       <td>",
"        Total 127 mg iodine per mL (&asymp;20 drops) of 100 mg/mL potassium iodide and 50 mg/mL iodine mixture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Antiarrhythmic drugs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Amiodarone",
"       </td>",
"       <td>",
"        75 mg iodine per 200 mg amiodarone*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Radiographic preparations",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Iodinated contrast agents",
"       </td>",
"       <td>",
"        Significant source of short-term iodine exposure. Typical oral preparations may contain 300 to 480 mg iodine per mL or tablet.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Intravenous preparations",
"       </td>",
"       <td>",
"        140 to 400 mg/mL",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Topical antiseptic agents",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Povidone-iodine solution 10 percent",
"       </td>",
"       <td>",
"        10 mg iodine per mL solution",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Iodine tinctures 2 and 7 percent",
"       </td>",
"       <td>",
"        20 mg iodine per mL of 2 percent tincture; 70 mg iodine per mL of 7 percent tincture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cadexomer-iodine dressings, ointment, powder, paste",
"       </td>",
"       <td>",
"        9 mg iodine per gram of cadexomer-iodine ointment, powder or paste",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Iodoquinol cream 1 percent",
"       </td>",
"       <td>",
"        6.4 mg iodine per gram of 1 percent cream (preparation may include 1 or 2 percent hydrocortisone)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Clioquinol cream",
"        <sup>",
"         &bull;",
"        </sup>",
"        3 percent",
"       </td>",
"       <td>",
"        12 mg iodine per gram of 3 percent cream",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Amebicidal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Iodoquinol (Yodoxin, Diodoquin)",
"       </td>",
"       <td>",
"        134 mg iodine per 210 mg iodoquinol oral capsule; 415 mg iodine per 650 mg iodoquinol oral capsule",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Douches",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Povidone-iodine",
"       </td>",
"       <td>",
"        10 mg iodine per mL solution",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Iodine containing expectorants",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Iodinated glycerol, calcium iodide",
"       </td>",
"       <td>",
"        Iodine has largely been discontinued as an ingredient of expectorants due to safety concerns. Typical preparations formerly available in the US contained approximately 6 to 25 mg iodine per mL.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Kelp or seaweed containing supplements",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Oral kelp (wrack, bladderwrack)",
"       </td>",
"       <td>",
"        Up to 6 mg of iodine may be contained in a 600 mg serving of kelp",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cellasene (anti-cellulite supplement)",
"       </td>",
"       <td>",
"        720 mcg of iodine per serving",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Approximately 10 percent is released as free inorganic iodine. Accumulation of amiodarone in body tissue results in a prolonged iodine exposure half-life (months or more) after discontinuation.",
"     <br>",
"      <span class=\"bullet\">",
"       &bull;",
"      </span>",
"      Clioquinal cream is not available as a single ingredient or combination product in the US. It is available in combinations with flumethasone as a topical cream and as otic in Canada.",
"      <br>",
"       &Delta; Iodine content is variable due to variability in source kelp and preparation.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
