var title_f18_32_18957="Muscular dystrophy PI";
var content_f18_32_18957=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PI%2F61217&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PI%2F61217&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Most common forms of muscular dystrophy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_left\" colspan=\"2\">",
"       Duchenne and Becker muscular dystrophy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       When do they start?",
"      </td>",
"      <td>",
"       Duchenne and Becker muscular dystrophy are very similar. Duchenne usually starts by age 2 or 3 years. Children with Duchenne are usually in a wheelchair by age 12. Becker causes similar symptoms but starts later and is usually less severe.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Who does it affect?",
"      </td>",
"      <td>",
"       Mostly boys, but it can affect girls some, too",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Which muscles are affected most by weakness?",
"      </td>",
"      <td>",
"       Weakness starts in the trunk but spreads to the arms and legs. The legs usually get weak first. This can make it hard to run, jump, or climb stairs.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       What other problems can it cause?",
"      </td>",
"      <td>",
"       <p>",
"        Heart and lung problems",
"       </p>",
"       <p>",
"        Abnormal curves in the spine",
"       </p>",
"       <p>",
"        Mental problems in some but not all children",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_left\" colspan=\"2\">",
"       Emery-Dreifuss muscular dystrophy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       When does it start?",
"      </td>",
"      <td>",
"       Ages 10 to 20 years",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Who does it affect?",
"      </td>",
"      <td>",
"       Boys and girls",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Which muscles are affected most?",
"      </td>",
"      <td>",
"       Weakness starts in the arms and later affects the legs, too. Sometimes the face weakens a little.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       What other problems can it cause?",
"      </td>",
"      <td>",
"       <p>",
"        Heart problems",
"       </p>",
"       <p>",
"        Limited movement at certain joints caused by a tightening of the tissues around the joint (called \"contracture\")",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_left\" colspan=\"2\">",
"       Myotonic dystrophy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       When does it start?",
"      </td>",
"      <td>",
"       Teenage years or later",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Who does it affect?",
"      </td>",
"      <td>",
"       Boys and girls",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Which muscles are affected most?",
"      </td>",
"      <td>",
"       Face muscles, arms, and legs",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       What other problems can it cause?",
"      </td>",
"      <td>",
"       <p>",
"        Muscle pain",
"       </p>",
"       <p>",
"        Problems affecting the heart, eyes, brain, and sexual organs",
"       </p>",
"       <p>",
"        Problems breathing and swallowing",
"       </p>",
"       <p>",
"        Stomach pain, diarrhea and constipation",
"       </p>",
"       <p>",
"        Being too sleepy during the day",
"       </p>",
"       <p>",
"        Nerve damage (called neuropathy)",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_left\" colspan=\"2\">",
"       Limb girdle muscular dystrophy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       When does it start?",
"      </td>",
"      <td>",
"       Childhood or later",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Who does it affect?",
"      </td>",
"      <td>",
"       Boys and girls",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Which muscles are affected most?",
"      </td>",
"      <td>",
"       Shoulders and hips",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       What other problems can it cause?",
"      </td>",
"      <td>",
"       <p>",
"        Heart problems",
"       </p>",
"       <p>",
"        Limited movement at certain joints caused by a tightening of the tissues around the joint (called \"contracture\")",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_left\" colspan=\"2\">",
"       Facioscapulohumeral muscular dystrophy (FSHD)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       When does it start?",
"      </td>",
"      <td>",
"       Usually affects people ages 20 to 30 years, but can happen younger",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Who does it affect?",
"      </td>",
"      <td>",
"       Boys and girls",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Which muscles are affected most?",
"      </td>",
"      <td>",
"       The face muscles get so weak that the person cannot smile, whistle, or shut his or her eyes tight. In some cases, weakness affects the legs; in other cases, the shoulders and upper arms.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       What other problems can it cause?",
"      </td>",
"      <td>",
"       <p>",
"        Vision or hearing problems",
"       </p>",
"       <p>",
"        Heart problems",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_left\" colspan=\"2\">",
"       Congenital muscular dystrophy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       When does it start?",
"      </td>",
"      <td>",
"       At birth",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Who does it affect?",
"      </td>",
"      <td>",
"       Boys and girls",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Which muscles are affected most?",
"      </td>",
"      <td>",
"       Many muscles can be weak. Sometimes this is called \"floppy baby\".",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       What other problems can it cause?",
"      </td>",
"      <td>",
"       Limited movement at certain joints caused by a tightening of the tissues around the joint (called \"contracture\")",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
