var title_f18_33_18972="Treatment of visceral leishmaniasis";
var content_f18_33_18972=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F80899&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F80899&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Treatment of visceral leishmaniasis in adults",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       &nbsp;",
"      </td>",
"      <td class=\"subtitle1\">",
"       Preferred therapy and duration",
"      </td>",
"      <td class=\"subtitle1\">",
"       Alternative therapy",
"      </td>",
"      <td class=\"subtitle1\">",
"       Other options/issues",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       VL in previously immunocompetent patient",
"      </td>",
"      <td>",
"       <p>",
"        Liposomal amphotericin B 3 mg/kg IV on days 1-5, 14 and 21; or",
"       </p>",
"       <p>",
"        3 mg/kg/day x 7-10 days; or",
"       </p>",
"       <p>",
"        10 mg/kg/day x 2 days",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        Amphotericin B desoxycholate 0.75-1.0 mg/kg IV every other day for 30 days; or",
"       </p>",
"       <p>",
"        Sodium stibogluconate* 20 mg/kg IV or IM daily x 28 days",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        Nutritional support, treatment of hemorrhagic or infectious complications",
"       </p>",
"       <p>",
"        Miltefosine&bull; 2.5 mg/kg/day PO x 28 days",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       HIV-VL coinfection",
"      </td>",
"      <td>",
"       <p>",
"        Liposomal amphotericin B 2-4 mg/kg IV daily x 10 days; or",
"       </p>",
"       <p>",
"        Interrupted schedule (e.g. 4 mg/kg on days 1-5, 10, 17, 24, 31, 38) to achieve total dose of 20-60 mg/kg",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        Amphotericin B desoxycholate 0.5-1.0 mg/kg IV QD for total dose of 1.5-2.0 grams; or",
"       </p>",
"       <p>",
"        Sodium stibogluconate* 20 mg/kg IV or IM daily x 28 days",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        ART should be initiated or optimized",
"       </p>",
"       <p>",
"        Alternative regimen for treatment failure: Miltefosine&bull; 2.5 mg/kg/day PO x 28 days",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Secondary prophylaxis: Liposomal amphotericin B 4 mg/kg every 2 to 4 weeks",
"      </td>",
"      <td>",
"       Secondary prophylaxis: Sodium stibogluconate* 20 mg/kg IV or IM every 4 weeks",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Post kala-azar dermal leishmaniasis",
"      </td>",
"      <td>",
"       Sodium stibogluconate* 20 mg/kg/day IV or IM x 60-120 days (usually given in 20 day courses with 10 day rest periods)",
"      </td>",
"      <td>",
"       Amphotericin B deoxycholate 1 mg/kg/day x 60 days (20-day courses with 20-day rest periods)",
"      </td>",
"      <td>",
"       Miltefosine&bull; 2.5 mg/kg/day PO x 12 weeks",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Available from CDC Division of Parasitic Diseases (Contact 404-718-4745; email parasites@cdc.gov, CDC Drug Service (404-639-3670), or for emergencies outside of business hours, CDC Emergency Operations Center (770-488-7100).",
"     <br>",
"      &bull; Not currently available in the United States.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
