" <div class=\"graphic\">",
"  <div class=\"figure\" style=\"width: 470px\">",
"   <div class=\"ttl\">",
"    Post operative fat necrosis presenting as a palpable lump",
"   </div>",
"   <div class=\"cntnt\" style=\"width: 314px; height: 423px; background-image: url(data:image/jpeg;base64,);\">",
"   </div>",
"   <div class=\"lgnd\">",
